import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Line, Bar } from 'react-chartjs-2';

function ComparativoFinanzas() {
  // Datos extraídos del archivo finanzas_publicas.md
  const chartData = {
    // Evolución del Balance Fiscal (% del PIB)
    balanceFiscal: {
      labels: ['2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021', '2022', '2023'],
      datasets: [
        {
          label: 'Resultado Operativo Bruto (% PIB)',
          data: [1.9, 0.8, 1.1, 1.9, 1.3, 1.3, 1.6, 3.0, 1.9, 1.4],
          borderColor: 'rgb(59, 130, 246)',
          backgroundColor: 'rgba(59, 130, 246, 0.5)',
          tension: 0.4,
        },
        {
          label: 'Préstamo Neto/Endeudamiento Neto (% PIB)',
          data: [1.0, 0.4, 0.2, 1.0, 0.5, 0.1, 1.4, 2.3, 0.8, 0.4],
          borderColor: 'rgb(16, 185, 129)',
          backgroundColor: 'rgba(16, 185, 129, 0.5)',
          tension: 0.4,
        },
      ],
    },
    // Evolución de la Deuda Pública (% del PIB)
    deudaPublica: {
      labels: ['2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021', '2022', '2023'],
      datasets: [
        {
          label: 'Deuda Bruta (% PIB)',
          data: [15.04, 17.39, 21.12, 23.68, 25.83, 28.36, 32.41, 36.45, 37.81, 39.43],
          borderColor: 'rgb(244, 63, 94)',
          backgroundColor: 'rgba(244, 63, 94, 0.5)',
          tension: 0.4,
        },
        {
          label: 'Pasivo Financiero Neto (% PIB)',
          data: [-4.4, -3.5, 0.9, 4.4, 5.7, 8.0, 13.3, 20.2, 20.4, 23.2],
          borderColor: 'rgb(139, 92, 246)',
          backgroundColor: 'rgba(139, 92, 246, 0.5)',
          tension: 0.4,
        },
      ],
    },
    // Comparativo de Ingresos y Gastos (Miles de millones de pesos de 2023)
    ingresosGastos: {
      labels: ['2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021', '2022', '2023'],
      datasets: [
        {
          type: 'bar',
          label: 'Ingresos Totales',
          data: [null, null, null, null, null, null, null, null, null, 70.830],
          backgroundColor: 'rgba(59, 130, 246, 0.7)',
          borderColor: 'rgb(59, 130, 246)',
          borderWidth: 1,
        },
        {
          type: 'bar',
          label: 'Gastos Totales',
          data: [null, null, null, null, null, null, null, null, null, 77.238],
          backgroundColor: 'rgba(244, 63, 94, 0.7)',
          borderColor: 'rgb(244, 63, 94)',
          borderWidth: 1,
        },
      ],
    },
    // Composición de Ingresos Tributarios 2023 (%)
    composicionIngresos: {
      labels: ['Ingresos Tributarios', 'Cobre Bruto', 'Imposiciones Previsionales', 'Otros'],
      datasets: [
        {
          data: [77.3, 1.7, 4.6, 16.4],
          backgroundColor: [
            'rgba(59, 130, 246, 0.7)',
            'rgba(245, 158, 11, 0.7)',
            'rgba(16, 185, 129, 0.7)',
            'rgba(139, 92, 246, 0.7)',
          ],
          borderColor: [
            'rgb(59, 130, 246)',
            'rgb(245, 158, 11)',
            'rgb(16, 185, 129)',
            'rgb(139, 92, 246)',
          ],
          borderWidth: 1,
        },
      ],
    },
    // Comparativo Empresas Públicas (Miles de millones de pesos de 2023)
    empresasPublicas: {
      labels: ['CODELCO', 'ENAP', 'Otras Empresas'],
      datasets: [
        {
          label: 'Ingresos 2023',
          data: [14.711, 9.462, 2.930],
          backgroundColor: 'rgba(59, 130, 246, 0.7)',
          borderColor: 'rgb(59, 130, 246)',
          borderWidth: 1,
        },
        {
          label: 'Resultado Operativo Bruto 2023',
          data: [2.954, 0.820, 0.062],
          backgroundColor: 'rgba(16, 185, 129, 0.7)',
          borderColor: 'rgb(16, 185, 129)',
          borderWidth: 1,
        },
      ],
    },
  };

  // Opciones para gráficos de línea
  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          boxWidth: 10,
          usePointStyle: true,
          pointStyle: 'circle',
        },
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
    scales: {
      x: {
        grid: {
          display: false,
        },
      },
      y: {
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
        },
        ticks: {
          callback: function(value) {
            return value.toLocaleString() + '%';
          },
        },
      },
    },
    interaction: {
      mode: 'nearest',
      axis: 'x',
      intersect: false,
    },
  };

  // Opciones para gráficos de barras
  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
    scales: {
      x: {
        grid: {
          display: false,
        },
      },
      y: {
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-2xl">Análisis Comparativo de Finanzas Públicas</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground mb-4">
            Comparación de los principales indicadores de finanzas públicas de Chile durante el período 2014-2023.
            Estos gráficos muestran tendencias y patrones en la gestión fiscal del país.
          </p>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Evolución del Balance Fiscal (% del PIB)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <Line data={chartData.balanceFiscal} options={lineOptions} />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Evolución de la Deuda Pública (% del PIB)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <Line data={chartData.deudaPublica} options={lineOptions} />
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Ingresos vs Gastos 2023 (MM$)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <Bar data={chartData.ingresosGastos} options={barOptions} />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Composición de Ingresos 2023 (%)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <Bar data={chartData.composicionIngresos} options={barOptions} />
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Comparativo Empresas Públicas 2023 (MM$)</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-[300px]">
            <Bar data={chartData.empresasPublicas} options={barOptions} />
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Análisis de Tendencias</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div>
              <h3 className="text-lg font-semibold mb-2">Tendencia de la Deuda Pública</h3>
              <p className="text-muted-foreground">
                La deuda bruta como porcentaje del PIB ha mostrado una tendencia creciente desde 2014, 
                aumentando de 15.04% a 39.43% en 2023. Este incremento refleja los desafíos fiscales 
                enfrentados por Chile, especialmente durante la pandemia de COVID-19 (2020-2021).
              </p>
            </div>
            
            <div>
              <h3 className="text-lg font-semibold mb-2">Empresas Públicas</h3>
              <p className="text-muted-foreground">
                CODELCO sigue siendo la empresa pública más importante en términos de contribución 
                al fisco, con ingresos de 14.711 MM$ en 2023. El resultado operativo bruto de las 
                empresas públicas ha mostrado volatilidad, alcanzando un máximo de 8.557 MM$ en 2021.
              </p>
            </div>
            
            <div>
              <h3 className="text-lg font-semibold mb-2">Balance Fiscal</h3>
              <p className="text-muted-foreground">
                El préstamo neto/endeudamiento neto ha fluctuado significativamente, con valores 
                positivos en la mayoría de los años, pero con una tendencia a la baja desde 2021. 
                En 2023, este indicador se situó en 0.4% del PIB.
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export default ComparativoFinanzas;
